import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isCartVisible: false,
};

const mainSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    toggleCartVisibility(state) {
      state.isCartVisible = !state.isCartVisible;
    },
  },
});

export const mainActions = mainSlice.actions;
export default mainSlice;
