import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { mainActions } from "./store/main-slice";
import StatusBarMessage from "./components/UI/StatusBarMessage";

function App() {
  const isCartVisible = useSelector((state) => state.main.isCartVisible);
  const cart = useSelector((state) => state.cart);
  const statusMessage = useSelector((state) => state.main.statusMessage);

  const dispatchFunc = useDispatch();

  useEffect(() => {
    const sendCartData = async () => {
      dispatchFunc(
        mainActions.showStatusMessage({
          status: "pending",
          title: "Sending Data",
          message: "Cart Data Sending to Server...",
        })
      );
      const response = await fetch(
        "https://react-learn-e675d-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("Sending Data Error");
      }

      dispatchFunc(
        mainActions.showStatusMessage({
          status: "success",
          title: "Data Sent",
          message: "Cart Data Is Successfully sent to Server...",
        })
      );
    };
    sendCartData().catch((e) => {
      dispatchFunc(
        mainActions.showStatusMessage({
          status: "Error",
          title: "Data Error",
          message: "Error occured while sending data",
        })
      );
    });
  }, [cart]);

  return (
    <Layout>
      {statusMessage && <StatusBarMessage />}
      {isCartVisible && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
