import Card from "../UI/Card";
import styles from "./ProductItem.module.css";
import { useDispatch, useSelector } from "react-redux";
import { cartActions } from "../../store/cart-slice";

const ProductItem = (props) => {
  const cart = useSelector((state) => state.cart);

  const dispatchFunc = useDispatch();
  const { id, title, price, description } = props;

  const addItemHandler = () => {
    const updatedItemsQuantity = cart.itemsQuantity + 1;
    const updatedItems = cart.items.slice();

    const existingItem = updatedItems.find((item) => item.id === id);

    if (existingItem) {
      const updatedExistingItem = { ...existingItem };
      updatedExistingItem.quantity++;
      updatedExistingItem.price = updatedExistingItem.price + price;

      const existingItemIndex = updatedItems.findIndex(
        (item) => item.id === id
      );
      updatedItems[existingItemIndex] = updatedExistingItem;
    } else {
      updatedItems.push({
        id,
        price,
        quantity: 1,
        totalPrice: price,
        title: title,
      });
    }

    const updatedCart = {
      itemsQuantity: updatedItemsQuantity,
      items: updatedItems,
    };

    dispatchFunc(cartActions.updateCart(updatedCart));
    // dispatchFunc(
    //   cartActions.addItem({
    //     id,
    //     title,
    //     price,
    //   })
    // );
  };

  return (
    <li className={styles.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={styles.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={styles.actions}>
          <button onClick={addItemHandler}>Добавить в Корзину</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
